select * from customer;


-- Total revenue generated by male vs female

select gender , SUM(purchase_amount) as revenue from customer group by gender

--which cust use a discount but still spent more than average purchase amount

select customer_id , purchase_amount from customer where discount_applied = 'Yes' and purchase_amount >= ( select avg(purchase_amount) from customer)

--which are top 5 products with highest average review rating

select item_purchased , round(avg(review_rating :: numeric) , 2) as "Average Product Rating" from customer 
group by item_purchased order by avg(review_rating) desc limit 5;

--compare avg of purchase amount between standard and express 

select shipping_type, round(avg(purchase_amount) , 2) from customer where shipping_type in ('Standard' , 'Express' )group by  shipping_type 

--do subscribed customer spend more? compare average spend and total revenue between subscribers and non-subscriber

select subscription_status , round(avg(purchase_amount) , 2) as avg_spend , count(customer_id) as total_customers , round(sum(purchase_amount) , 2) as total_revenue 
from customer 
group by subscription_status order by total_revenue , avg_spend desc

-- which 5 products have highest  percentage of purchases with discount applied

select item_purchased , round(100 * sum(case when discount_applied = 'Yes' then 1 else 0 end)/count(*)  , 2) as discount_rate
from customer group by item_purchased order by discount_rate desc
limit 5

-- segment customer in basis of new , returning , loyal based on their number of previous purchase and show count of each segment

with customer_type as 
(
select customer_id , previous_purchases ,
case
when previous_purchases = 1 then 'New'
when previous_purchases between 2 and 10 then 'Returning'
else 'Loyal'
end as customer_segment 
from customer)
select customer_segment , count(*) as "Number of Customers" 
from customer_type group by customer_segment

-- which are top 3 purchased product within each category

with item_counts as (
select category ,
item_purchased , count(customer_id) as total_orders ,
row_number() over(partition by category order by count(customer_id) desc ) as item_rank
from customer group by category, item_purchased
)
select item_rank , category , item_purchased , total_orders from item_counts where item_rank <= 3

-- are customer who are repeat buyer(more than 5 previous purchase) also likely to subscribe

select subscription_status , count(customer_id) as repeat_buyers from customer
where previous_purchases > 5 group by subscription_status

-- what is revenue contribution of each age group
select age_group, sum(purchase_amount) as total_revenue from customer
group by age_group order by total_revenue desc;